mapper.select:
SELECT o.order_cd orderCode, ui.name customerName, prod.prod_nm productName, mct.m_ct_nm middleCategory, o.refund_date refundDate, o.refund_cnt refundCount, o.refund_amt refundAmount, dc.detail_name state FROM tb_order o JOIN ( SELECT detail_code, group_code, detail_name FROM tb_detail_code WHERE group_code = 'STTcd' ) dc ON o.STTcd = dc.detail_code LEFT JOIN tb_userinfo ui ON ui.loginID = o.loginID LEFT JOIN tb_scm_product prod ON prod.product_cd = o.order_cd LEFT JOIN tb_scm_m_category mct ON mct.m_ct_cd = prod.m_ct_cd WHERE STTcd IN (3, 4, 5, 6, 7)
AND DATE_FORMAT(o.refund_date, '%Y-%m-%d') BETWEEN #{startDate} AND #{endDate}
AND o.STTcd IN
#{state}
AND ui.name LIKE CONCAT('%', #{keyword}, '%') OR prod.prod_nm LIKE CONCAT('%', #{keyword}, '%')
AND ui.name LIKE CONCAT('%', #{keyword}, '%')
AND prod.prod_nm LIKE CONCAT('%', #{keyword}, '%')
ORDER BY CAST(o.STTcd as unsigned), o.refund_date DESC LIMIT #{pageIndex}, #{pageSize}

SELECT name
FROM tb_userinfo
WHERE loginID = #{loginId}

mapper.update:
UPDATE tb_order
SET STTcd = 4
WHERE order_cd = #{orderCode}

mapper.insert:
INSERT INTO tb_scm_refund_list (
									order_cd,
									scm_id,
									submit_date
 		                        )
VALUES (
			#{orderCode},
			#{loginId},
			#{refundDirectionDate}
		)
