mapper.update:
UPDATE tb_scm_warehouse 
SET  warehouse_nm = #{warehouse_nm}
   , wh_mng_id = #{wh_mng_id}
   , zip_cd = #{zip_cd}
   , addr = #{addr}
   , addr_detail = #{addr_detail}
WHERE warehouse_cd = #{warehouse_cd}

mapper.delete:
DELETE
FROM tb_scm_warehouse 
WHERE warehouse_cd = #{warehouse_cd}
AND (
      SELECT COUNT(*) 
      FROM tb_scm_product 
      WHERE warehouse_cd = #{warehouse_cd}
    ) = 0
AND (
      SELECT COUNT(*)
      FROM tb_scm_supply
      WHERE warehouse_cd = #{warehouse_cd}
    ) = 0
